import './App.css';
import Search from "./components/Search";
import ProductsList from './components/ProductsList';
import { useState , useEffect } from 'react'
import CartList from './components/CartList';
import axios from "axios"

const App = () => {
const [menuView, setMenuView] = useState(false);
const [view,setView] = useState("productList");
const [data , setData] = useState([])
const [computers , setComputers] = useState([])
const [phones , setPhones] = useState([])
const [electronics , setElectronics] = useState([])


useEffect(() => {
  axios.get("http://localhost:5000/api/products").then((resp) => {
    setComputers(resp.data.filter((e)=> e.category==='computers'))
    setPhones(resp.data.filter((e)=> e.category==='phones'))
    setElectronics(resp.data.filter((e)=> e.category==='electronics'))})
    
}, []);


const toggleMenu = ()=> {
  setMenuView(!menuView)
}
const switchView = (x) => {
  setView(x)
}
  return (
    <div className="App">
        <div className="nav">
          <span className="logo" onClick={()=>switchView ("productList")}>TEK STORE</span>
          { view ==="productList" &&<Search />}
        { view ==="productList" && <span className="items" onClick={toggleMenu}>
          &#9660;
            CATEGORIES
            &#9660;
          </span>}
          <span className="items" onClick={()=>switchView ("cart")}>
            ðŸ›’
            CART
          </span>
        </div>
       {menuView && <div className="menu">
            <span className='menu-item' onClick={()=>setData(computers)}>computers</span>
            <span className='menu-item' onClick={()=>setData(phones)}>phones</span>
            <span className='menu-item' onClick={()=>setData(electronics)}>electronics</span>
          </div>}

          {view ==="productList" && <ProductsList products={data}/>}
          {view ==="cart"&&<CartList/>}   
    </div>
  );
}

export default App;
